mapper.insert:
insert into code(
cname
,ccorder
,codegroup_ccgSeq
,yn
)
values(
#{cname}
,#{ccorder}
,#{codegroup_ccgSeq}
,#{yn}
)

	select last_insert_id()

mapper.update:
update code
set
	cname = #{cname}
	,ccorder = #{ccorder}
	,yn = #{yn}
	,uesrNY = #{userNY}
	,codegroup_ccgSeq = #{codegroup_ccgSeq}
		WHERE
			ccseq = #{ccseq }
