mapper.insert:
insert into codegroup (
		ccorder
		,codeName
		,userNY
		,delNy
		,codeNameEng
		,regDate
	)
	values(
		#{ccorder}
		,#{codeName}
		,#{userNY}
		,#{delNy}
		,#{codeNameEng}
		,NOW()
	)

	select last_insert_id()

mapper.update:
UPDATE codegroup
SET	
	ccorder = #{ccorder}
	,codeName = #{codeName}
	,userNY = #{userNY}
	,codeNameEng = #{codeNameEng}
	,modifyDate = NOW()
	,delNy = #{delNy}
WHERE
	ccgseq = #{ccgseq }

mapper.sql:
FROM codegroup a
WHERE 1=1
	 AND a.regDate between #{shstartDate} AND #{shendDate}


		AND ccgSeq = #{shValue}
		AND codeName LIKE CONCAT('%',#{shValue},'%')
		AND codeNameEng LIKE CONCAT('%',#{shValue},'%')
		AND delNy = #{shDelNy}
		AND delNy = #{shDelNy}
