mapper.insert:
insert into member(
memberID
,memberPW
,memberNick
,memberName
,memberGender
,memberEmail
,memberMobile
,memberDelNy
,memberAd
,memberGrade
,memberRegdate
,memberPoint
)
values(
#{memberID}
,#{memberPW}
,#{memberNick}
,#{memberName}
,#{memberGender}
,#{memberEmail}
,#{memberMobile}
,0
,#{memberAd}
,1
,now()
,#{memberPoint}
)

	select last_insert_id()

mapper.update:
UPDATE member
SET	
	memberID = #{memberID}
	,memberPW = #{memberPW}
	,memberNick = #{memberNick}
	,memberName = #{memberName}
	,memberDob = #{memberDob}
	,memberEmail = #{memberEmail}
	,memberMobile = #{memberMobile}
	,memberDelNy = 1
	,memberGrade = #{memberGrade}
	,memberGender = #{memberGender} 
	,memberPoint = #{memberPoint}
	,memberAd = #{memberAd}
where
	memberSeq = #{memberSeq}

