mapper.insert:
    	insert into reView(
    	member_memberSeq
    	,product_Seq
    	,Score
    	,Content
    	,reviewDelNy
    	,reviewRegDate
    	) values (
    	#{member_memberSeq}
    	,#{product_Seq}
    	,#{Score}
    	,#{Content}
    	,0
    	,now()
    	)

			select last_insert_id()

    	insert into product(
    		productName
    		,productType
    		,productPrice
    		,productNew
    		,productBest
    		,productRegDate

    		,informationColor
    		,informationSize
    		,informationCompany
    		,informationWarning
    		,informationCountry
    		,informationQuality
    		,informationAS
    		,productDelNy
    		)
    		VALUES(
    		 #{productName}
    		,#{productType}
    		,#{productPrice}
    		,#{productNew}
    		,#{productBest}
    		,now()

    		,#{informationColor}
    		,#{informationSize}
    		,#{informationCompany}
    		,#{informationWarning}
    		,#{informationCountry}
    		,#{informationQuality}
    		,#{informationAS}
    		,#{productDelNy}
    		)

			select last_insert_id()

    	insert into productDetail(
    		product_Seq
    		,color_colorseq
    		,size_size
    		,num
    	)
    	VALUES(
    		#{productSeq}
    		,#{color_colorseq}
    		,#{size_size}
    		,#{num}
    	)

			select last_insert_id()

mapper.update:
    	update product
    	set
    	    productName = #{productName}
    		,productType = #{productType}
    		,productPrice = #{productPrice}
    		,productNew = #{productNew}
    		,productBest = #{productBest}
    		,productRegDate = #{productRegDate}
    		,informationType = #{informationType}
    		,informationColor = #{informationColor}
    		,informationSize = #{informationSize}
    		,informationCompany = #{informationCompany}
    		,informationCountry = #{informationCountry}
    		,informationWarning = #{informationWarning}
    		,informationQuality = #{informationQuality}
    		,informationAS = #{informationAS}
    		,productDelNy = #{productDelNy}
    		where
			productSeq = #{productSeq}

