mapper.sql:
    	from CodeGroup a
    	WHERE 1=1

			 AND codeGroupRegdate between #{shstartDate} AND #{shendDate}


				AND codeGroupSeq = #{shValue}
				AND codeGroupName LIKE CONCAT('%',#{shValue},'%')
				AND codeGroupNameEng LIKE CONCAT('%',#{shValue},'%')
				AND codeGroupDelNY = #{shDelNy}
				AND codeGroupDelNY = #{shDelNy}

mapper.insert:
insert into CodeGroup (
	codeGroupSeq
	,codeGroupName
	,codeGroupNameEng
	,codeGroupOrder
	,codeGroupUseNY
	,codeGroupDelNY
	,codeGroupRegdate
	)
	values(
	 #{codeGroupSeq}
	,#{codeGroupName}
	,#{codeGroupNameEng}
	,#{codeGroupOrder}
	,#{codeGroupUseNY}
	,#{codeGroupDelNY}
	,now()
	)


	select last_insert_id()

mapper.update:
UPDATE CodeGroup
SET	
	codeGroupName = #{codeGroupName }
	,codeGroupNameEng = #{codeGroupNameEng }
	,codeGroupOrder = #{codeGroupOrder }
	,codeGroupUseNY = #{codeGroupUseNY }
	,codeGroupDelNY = #{codeGroupDelNY }
	,codeGroupUpdate = now()
WHERE
	codeGroupSeq = #{codeGroupSeq}
