mapper.insert:
insert into Code(
	codeName
	,cGroupSeq
	,codeNameEng
	,codeOrder
	,codeUseNY
	,codeDelNY
	,codeRegdate
)
values(
	#{codeName}
	,#{cGroupSeq}
	,#{codeNameEng}
	,#{codeOrder}
	,#{codeUseNY}
	,#{codeDelNY}
	,now()
)

	select last_insert_id()

mapper.update:
update Code
set
	codeName = #{codeName}
	,cGroupSeq = #{cGroupSeq}
	,codeNameEng = #{codeNameEng}
	,codeOrder = #{codeOrder}
	,codeUseNY = #{codeUseNY}
	,codeDelNY = #{codeDelNY}
	,codeUpdate = now()
where 
	codeSeq = #{codeSeq}
